<?phpnamespace app\backend;use Yii;use yii\base\Module;use yii\base\BootstrapInterface;use yii\web\Application;use yii\web\View;class BackendModule extends Module implements BootstrapInterface{		public function bootstrap($app)	{		$app->on(			Application::EVENT_BEFORE_ACTION,			function() use ($app){				if(					Yii::$app->request->isAjax === false &&					Yii::$app->requestedAction->controller->module instanceof BackendModule === false				){					if(Yii::$app->user->can('administrate')){						$app->getView()->on(							View::EVENT_BEGIN_BODY,							function (){															}						);					}				}			}		);	}}